mapper.insert:

			SELECT  CODE_NAME 
                 FROM COM c 
                 WHERE COMMON_CODE =#{apd_form}


		 INSERT INTO AP_FAV (APF_ID, EMP_NO, APD_FORM,
	                         REG_ID, REG_DATE, MOD_ID, 
	                         MOD_DATE,APF_NAME )
        			VALUES (FAV_SEQ.NEXTVAL, #{emp_no}, #{apd_form}, 
           					#{emp_no}, SYSDATE, #{emp_no}, 
           					SYSDATE, #{name}
    						)

mapper.delete:
DELETE FROM AP_FAV
 WHERE EMP_NO = #{emp_no}
 AND APD_FORM=#{apd_form}

mapper.update:
UPDATE AP_LINE SET COL_TYPE =
1
2
, FILE_ONAME = #{file_oname}
, APL_STATUS = 1, MOD_DATE = SYSDATE WHERE APL_NO = #{apl_no}

UPDATE AP_DOCU SET APD_STATUS=#{apd_status}, APD_CLEAR_DATE=SYSDATE,
					MOD_DATE=SYSDATE,MOD_ID=#{emp_no}
	WHERE APD_NO=#{apd_no}

mapper.select:
SELECT APD_STEP 
 FROM AP_DOCU ad
 WHERE APD_NO = #{apd_no}

SELECT APL_ORDER
 FROM AP_LINE al 
 WHERE APD_NO =#{apd_no}
 AND EMP_NO =#{emp_no}

SELECT APL_NO 
 FROM AP_LINE al
 WHERE al.APD_NO = #{apd_no}
 AND EMP_NO =#{emp_no}

