mapper.update:
UPDATE VA_check
SET va_check_num = (SELECT VA_CHECK_NUM - #{minus}
                    FROM VA_check 
                    WHERE emp_no =  #{submit_id} 
                    AND TO_CHAR(VA_YEAR, 'YYYY') = #{year})
